    fun isCanMove(user: User, direction: MoveDirection): Boolean {
        when (direction) {
            MoveDirection.UP -> {
                if (map[user.y - 1][user.x] == 0) {
                    return true
                }
            }
            MoveDirection.DOWN -> {
                if (map[user.y + 1][user.x] == 0) {
                    return true
                }
            }
            MoveDirection.LEFT -> {
                if (map[user.y][user.x - 1] == 0) {
                    return true
                }
            }
            MoveDirection.RIGHT -> {
                if (map[user.y][user.x + 1] == 0) {
                    return true
                }
            }
            else -> return false
        }

        return false
    }
